{
  "compilerOptions": {
    "target": "ES2020",
    "useDefineForClassFields": true,
    "module": "ESNext",
    "moduleResolution": "Bundler",
    "jsx": "react-jsx",
    "resolveJsonModule": true,

    "allowJs": true,
    "checkJs": false,

    "isolatedModules": true,
    "noEmit": true,

    "strict": false,
    "noImplicitAny": false,
    "skipLibCheck": true,

    "baseUrl": ".",
    "paths": {
      "@/*": ["src/*"]
    },

    "types": ["vite/client"]
  },
  "include": ["src", "vite.config.ts", "env.d.ts"],
  "exclude": ["node_modules", "dist"]
}
